import { NgModule } from '@angular/core';
import { RouterModule, Routes } from '@angular/router';
import { NotfoundComponent } from './demo/components/notfound/notfound.component';
import { AppLayoutComponent } from './layout/app.layout.component';
import { GlobalGuardsGuard } from './guards/global-guards.guard';
import { DashboardComponent } from './demo/components/dashboard/dashboard.component';
import { RecoverPasswordComponent } from './views/recover-password/recover-password.component';
import { LoginComponent } from './views/login/login.component';

const routes: Routes = [
    { path: '', redirectTo: '/login', pathMatch: 'full' }, // Redirecciona a la página de inicio de sesión por defecto

    {
        path: '',
        component: AppLayoutComponent,
        canActivate: [GlobalGuardsGuard], // Guardia de ruta para proteger esta sección
        children: [
            { path: 'dashboard', component: DashboardComponent }, // Ruta para el dashboard
            { path: 'recover-password', component: RecoverPasswordComponent }, // Ruta para recuperar contraseña
            { path: 'notfound', component: NotfoundComponent }, // Ruta para la página no encontrada
            { path: '**', redirectTo: '/notfound' } // Redirección para rutas no encontradas
        ]
    }
];

@NgModule({
    imports: [
        RouterModule.forRoot(routes, {
            scrollPositionRestoration: 'enabled',
            anchorScrolling: 'enabled',
            onSameUrlNavigation: 'reload'
        })
    ],
    exports: [RouterModule]
})
export class AppRoutingModule { }
