import { NgModule } from '@angular/core';
import { RouterModule, Routes } from '@angular/router';
import { NotfoundComponent } from './demo/components/notfound/notfound.component';
import { AppLayoutComponent } from "./layout/app.layout.component";
import { GlobalGuardsGuard } from "./guards/global-guards.guard";
import { DashboardComponent } from "./demo/components/dashboard/dashboard.component";
import { RecoverPasswordComponent } from "./views/recover-password/recover-password.component";
import { LoginComponent } from "./views/login/login.component";

const routes: Routes = [
    {
        path: '', component: AppLayoutComponent,
        children: [
            { path: 'dashboard', canActivate: [GlobalGuardsGuard], component: DashboardComponent },
        ]
    },
    { path: 'notfound', component: NotfoundComponent },
    { path: 'login', component: LoginComponent },
    { path: 'recover-password', component: RecoverPasswordComponent },
    { path: '**', redirectTo: '/notfound' },
];

@NgModule({
    imports: [
        RouterModule.forRoot(routes, { scrollPositionRestoration: 'enabled', anchorScrolling: 'enabled', onSameUrlNavigation: 'reload' })
    ],
    exports: [RouterModule]
})
export class AppRoutingModule { }
