import { NgModule } from '@angular/core';
import { BrowserModule } from '@angular/platform-browser';
import { RouterModule, Routes } from '@angular/router';
import { HttpClientModule } from '@angular/common/http';
import { FormsModule, ReactiveFormsModule } from '@angular/forms';

// Importa tus componentes
import { AppComponent } from './app.component';
import { LoginComponent } from './views/login/login.component';
import { DashboardComponent } from './demo/components/dashboard/dashboard.component';
import { RecoverPasswordComponent } from './views/recover-password/recover-password.component';
import { NotfoundComponent } from './demo/components/notfound/notfound.component';

import { GlobalGuardsGuard } from './guards/global-guards.guard';

// Define las rutas
const appRoutes: Routes = [
    { path: 'login', component: LoginComponent },
    { path: 'dashboard', component: DashboardComponent },
    { path: 'recover-password', component: RecoverPasswordComponent },
    { path: 'notfound', component: NotfoundComponent },
    { path: '**', redirectTo: '/notfound' }
];

@NgModule({
    declarations: [
        AppComponent,
        LoginComponent,
        DashboardComponent,
        RecoverPasswordComponent,
        NotfoundComponent
    ],
    imports: [
        RouterModule,
        BrowserModule,
        HttpClientModule,
        FormsModule,
        ReactiveFormsModule,
        RouterModule.forRoot(appRoutes, { enableTracing: true }) // Habilita el tracing si lo necesitas
    ],
    exports: [RouterModule],
    providers: [
        GlobalGuardsGuard // Agrega tus servicios y guardias aquí si los necesitas
    ],
    bootstrap: [AppComponent] // Componente raíz
})
export class AppModule { }
